// PassengerDetails.js
import React, { useState, useEffect } from 'react';

function PassengerDetails({ passenger, updatePassenger }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [flights, setFlights] = useState('');

    useEffect(() => {
        if (passenger) {
            setFirstName(passenger.firstName);
            setLastName(passenger.lastName);
            setFlights(passenger.flights.join(', '));
        }
    }, [passenger]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const passengerRequest = {
            firstName,
            lastName,
            flights: flights.split(',').map(flight => parseInt(flight.trim()))
        };
        await updatePassenger(passenger.id, passengerRequest);
    };

    if (!passenger) return <div>Wybierz pasażera do edytowania</div>;

    return (
        <div>
            <h2>Edytuj pasażera</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    placeholder="Imię"
                    required
                />
                <input
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    placeholder="Nazwisko"
                    required
                />
                <input
                    type="text"
                    value={flights}
                    onChange={(e) => setFlights(e.target.value)}
                    placeholder="Numer lotu"
                    required
                />
                <button type="submit">Zaktualizuj</button>
            </form>
        </div>
    );
}

export default PassengerDetails;
