import React, { useState } from 'react';
import axios from 'axios';

function WeatherDetails({ fetchWeatherByCity }) {
    const [cityName, setCityName] = useState('');
    const [weatherDetails, setWeatherDetails] = useState(null);

    const handleChange = (e) => {
        setCityName(e.target.value);
    };

    const handleFetchDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/weather/${cityName}`);
            setWeatherDetails(response.data);
        } catch (error) {
            console.error('Błąd pobierania szczegółów pogody:', error);
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Wprowadź nazwę miasta"
                value={cityName}
                onChange={handleChange}
            />
            <button onClick={handleFetchDetails}>Pobierz szczegóły pogody</button>
            {weatherDetails && (
                <div>
                    <h4>Pogoda dla {weatherDetails.cityName}</h4>
                    <p>Opis: {weatherDetails.description}</p>
                    <p>Temperatura: {weatherDetails.temperature}°C</p>
                    <p>Wiatr: {weatherDetails.windSpeed} m/s</p>
                    <p>Wilgotność: {weatherDetails.humidity}%</p>
                </div>
            )}
        </div>
    );
}

export default WeatherDetails;
