import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CityForm from './CityForm';
import CityList from './CityList';
import CityDetails from './CityDetails';
import AirportForm from './AirportForm'; // Nowy komponent do tworzenia lotniska
import AirportList from './AirportList'; // Nowy komponent do listy lotnisk
import AirportDetails from './AirportDetails'; // Nowy komponent do szczegółów lotniska

function App() {
    const [cities, setCities] = useState([]);
    const [cityById, setCityById] = useState(null);
    const [airports, setAirports] = useState([]); // Nowy stan dla lotnisk
    const [airportById, setAirportById] = useState(null); // Nowy stan dla lotniska po ID
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchCities();
        fetchAirports();
    }, []);

    const fetchCities = async () => {
        try {
            const response = await axios.get('http://localhost:8080/cities/getAllCities');
            setCities(response.data);
        } catch (error) {
            console.error('Błąd pobierania miast:', error);
            setMessage('Nie udało się pobrać miast.');
        }
    };

    const fetchCityById = async (id) => {
        try {
            const response = await axios.get(`http://localhost:8080/cities/getCityById/${id}`);
            setCityById(response.data);
        } catch (error) {
            console.error('Błąd pobierania miasta:', error);
            setMessage('Nie udało się pobrać miasta.');
        }
    };

    // Nowa funkcja do pobierania wszystkich lotnisk
    const fetchAirports = async () => {
        try {
            const response = await axios.get('http://localhost:8080/airports/getAllAirports');
            setAirports(response.data);
        } catch (error) {
            console.error('Błąd pobierania lotnisk:', error);
            setMessage('Nie udało się pobrać lotnisk.');
        }
    };

    // Nowa funkcja do pobierania lotniska po ID
    const fetchAirportById = async (id) => {
        try {
            const response = await axios.get(`http://localhost:8080/airports/getAirportById/${id}`);
            setAirportById(response.data);
        } catch (error) {
            console.error('Błąd pobierania lotniska:', error);
            setMessage('Nie udało się pobrać lotniska.');
        }
    };

    return (
        <div className="App">
            <h1>Miasta i Lotniska</h1>
            {message && <p>{message}</p>}

            {/* Wyświetlanie listy miast */}
            <CityList cities={cities} />

            {/* Formularz do dodawania miast */}
            <CityForm fetchCities={fetchCities} />

            {/* Formularz do dodawania lotnisk */}
            <AirportForm fetchAirports={fetchAirports} />

            {/* Lista lotnisk */}
            <AirportList airports={airports} />

            {/* Szczegóły lotniska */}
            <AirportDetails fetchAirportById={fetchAirportById} airport={airportById} />

            {/* Szczegóły miasta */}
            <CityDetails fetchCityById={fetchCityById} city={cityById} />
        </div>
    );
}

export default App;
