import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FlightDetails({ fetchFlightById, flight }) {
    const [id, setId] = useState('');
    const [departureAirport, setDepartureAirport] = useState(null);
    const [arrivalAirport, setArrivalAirport] = useState(null);

    useEffect(() => {
        if (flight) {
            // Fetch airports once flight details are available
            fetchAirportDetails(flight.departureAirportId, 'departure');
            fetchAirportDetails(flight.arrivalAirportId, 'arrival');
        }
    }, [flight]); // Re-run if flight changes

    const handleChange = (e) => {
        setId(e.target.value);
    };

    const handleFetchFlight = () => {
        // Check if ID is valid before calling the API
        const parsedId = parseInt(id, 10);
        if (!isNaN(parsedId)) {
            fetchFlightById(parsedId);  // Fetch flight by ID
        } else {
            alert('Podaj poprawne ID lotu');
        }
    };

    // Function to fetch airport details
    const fetchAirportDetails = async (airportId, type) => {
        try {
            const response = await axios.get(`http://localhost:8080/airports/getAirportById/${airportId}`);
            if (type === 'departure') {
                setDepartureAirport(response.data);
            } else if (type === 'arrival') {
                setArrivalAirport(response.data);
            }
        } catch (error) {
            console.error(`Błąd pobierania lotniska: ${airportId}`, error);
        }
    };

    return (
        <div>
            <h2>Szukaj lotu po ID</h2>
            <input
                type="text"
                value={id}
                onChange={handleChange}
                placeholder="Wprowadź ID lotu"
            />
            <button onClick={handleFetchFlight}>Pobierz lot</button>

            {flight ? (
                <div>
                    <h3>Lot: {flight.flightNumber}</h3>
                    <p>Wylot: {flight.departureTime}</p>
                    <p>Status: {flight.status}</p>

                    {/* Display departure airport info if available */}
                    <p>Lotnisko odlotu:
                        {departureAirport ? `${departureAirport.name}, ${departureAirport.city}, ${departureAirport.country}` : 'Ładowanie...'}
                    </p>

                    {/* Display arrival airport info if available */}
                    <p>Lotnisko przylotu:
                        {arrivalAirport ? `${arrivalAirport.name}, ${arrivalAirport.city}, ${arrivalAirport.country}` : 'Ładowanie...'}
                    </p>
                </div>
            ) : (
                <p>Nie znaleziono lotu o tym ID</p>
            )}
        </div>
    );
}

export default FlightDetails;
