import React, { useState, useEffect } from 'react';

function FlightDetails({ flight, fetchFlightById, fetchFlightsByStatus, updateFlight, deleteFlight }) {
    const [id, setId] = useState('');
    const [notFound, setNotFound] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [searchStatus, setSearchStatus] = useState(''); // Nowy stan dla wyszukiwania po statusie
    const [flightsByStatus, setFlightsByStatus] = useState([]); // Wyniki wyszukiwania po statusie

    const [updatedFlight, setUpdatedFlight] = useState({
        flightNumber: '',
        flightLength: '',
        departureTime: '',
        departureAirportId: '',
        arrivalAirportId: '',
        status: ''  // Dodane pole statusu do aktualizacji
    });

    const handleChange = (e) => {
        setId(e.target.value);
        setNotFound(false);
        setError('');
    };

    const handleStatusChange = (e) => {
        setSearchStatus(e.target.value);
    };

    const handleUpdateChange = (e) => {
        const { name, value } = e.target;
        setUpdatedFlight({
            ...updatedFlight,
            [name]: value
        });
    };

    const handleFetchFlight = async () => {
        const parsedId = parseInt(id, 10);

        if (!id || isNaN(parsedId)) {
            setError('Podaj poprawne ID lotu');
            return;
        }

        setNotFound(false);
        setLoading(true);

        try {
            const response = await fetchFlightById(parsedId);

            if (response && response.id) {
                setUpdatedFlight({
                    flightNumber: response.flightNumber,
                    flightLength: response.flightLength,
                    departureTime: response.departureTime,
                    departureAirportId: response.departureAirportId,
                    arrivalAirportId: response.arrivalAirportId,
                    status: response.status
                });
            } else {
                setNotFound(true);
            }
        } catch (error) {
            setNotFound(true);
            setError('Wystąpił błąd przy pobieraniu danych lotu');
        }

        setLoading(false);
    };

    const handleSearchByStatus = async () => {
        if (!searchStatus.trim()) {
            setError('Podaj status do wyszukiwania');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const flights = await fetchFlightsByStatus(searchStatus);

            if (flights && flights.length > 0) {
                setFlightsByStatus(flights);
            } else {
                setFlightsByStatus([]);
                setError('Nie znaleziono lotów dla podanego statusu');
            }
        } catch (error) {
            setError('Wystąpił błąd przy wyszukiwaniu lotów po statusie');
        }

        setLoading(false);
    };

    const handleUpdateFlight = async () => {
        const parsedId = parseInt(id, 10);
        if (isNaN(parsedId)) {
            setError('Podaj poprawne ID lotu do aktualizacji');
            return;
        }

        if (!updatedFlight.flightNumber || !updatedFlight.departureTime || !updatedFlight.arrivalAirportId || !updatedFlight.departureAirportId || !updatedFlight.status) {
            setError('Wszystkie pola muszą być wypełnione.');
            return;
        }

        try {
            await updateFlight(parsedId, updatedFlight);
            alert('Lot został zaktualizowany');
            window.location.reload();
        } catch (error) {
            setError('Wystąpił błąd podczas aktualizacji lotu');
        }
    };

    return (
        <div>
            <h2>Szukaj lotu po ID</h2>
            <input
                type="text"
                value={id}
                onChange={handleChange}
                placeholder="Wprowadź ID lotu"
            />
            <button onClick={handleFetchFlight}>Pobierz lot</button>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {flight && (
                <div>
                    <h3>Lot: {flight.flightNumber}</h3>
                    <p>Status: {flight.status}</p>
                    <p>Wylot: {flight.departureTime}</p>
                </div>
            )}

            <h3>Wyszukaj loty po statusie</h3>
            <input
                type="text"
                value={searchStatus}
                onChange={handleStatusChange}
                placeholder="Wprowadź status lotu"
            />
            <button onClick={handleSearchByStatus}>Szukaj</button>

            {flightsByStatus.length > 0 && (
                <div>
                    <h4>Wyniki wyszukiwania:</h4>
                    <ul>
                        {flightsByStatus.map((flight) => (
                            <li key={flight.id}>
                                Lot {flight.flightNumber} ({flight.status})
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            <h3>Aktualizuj lot</h3>
            <input
                type="text"
                name="flightNumber"
                value={updatedFlight.flightNumber}
                onChange={handleUpdateChange}
                placeholder="Numer lotu"
            />
            <input
                type="number"
                name="flightLength"
                value={updatedFlight.flightLength}
                onChange={handleUpdateChange}
                placeholder="Długość lotu (godziny)"
            />
            <input
                type="datetime-local"
                name="departureTime"
                value={updatedFlight.departureTime}
                onChange={handleUpdateChange}
                placeholder="Czas wylotu"
            />
            <input
                type="number"
                name="departureAirportId"
                value={updatedFlight.departureAirportId}
                onChange={handleUpdateChange}
                placeholder="ID lotniska odlotu"
            />
            <input
                type="number"
                name="arrivalAirportId"
                value={updatedFlight.arrivalAirportId}
                onChange={handleUpdateChange}
                placeholder="ID lotniska przylotu"
            />
            <input
                type="text"
                name="status"
                value={updatedFlight.status}
                onChange={handleUpdateChange}
                placeholder="Status"
            />
            <button onClick={handleUpdateFlight}>Zaktualizuj lot</button>
        </div>
    );
}

export default FlightDetails;
