import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FlightDetails({ fetchFlightById, flight }) {
    const [id, setId] = useState('');
    const [departureAirport, setDepartureAirport] = useState(null);
    const [arrivalAirport, setArrivalAirport] = useState(null);
    const [notFound, setNotFound] = useState(false); // Stan kontrolujący komunikat "Nie znaleziono lotu"
    const [loading, setLoading] = useState(false); // Stan kontrolujący ładowanie

    useEffect(() => {
        // Tylko, gdy lot jest załadowany, pobieramy lotniska
        if (flight) {
            console.log("Dane lotu:", flight); // Debugowanie danych lotu

            // Resetujemy stan błędu, jeśli lot został załadowany
            setNotFound(false);
            setLoading(false); // Po załadowaniu przestajemy ładować

            // Sprawdzanie czy lotnisko odlotu jest dostępne i pobieramy dane
            if (flight.departureAirport) {
                setDepartureAirport(flight.departureAirport);
            } else {
                console.log("Brak danych o lotnisku odlotu");
            }

            // Sprawdzanie czy lotnisko przylotu jest dostępne i pobieramy dane
            if (flight.arrivalAirport) {
                setArrivalAirport(flight.arrivalAirport);
            } else {
                console.log("Brak danych o lotnisku przylotu");
            }
        }
    }, [flight]); // Re-run if flight changes

    const handleChange = (e) => {
        setId(e.target.value);
        setNotFound(false); // Resetujemy komunikat o błędzie, gdy użytkownik zmienia ID
    };

    const handleFetchFlight = () => {
        const parsedId = parseInt(id, 10);
        console.log("Przekazywane ID:", parsedId); // Debugowanie ID

        if (!isNaN(parsedId)) {
            setNotFound(false);  // Resetujemy komunikat przed wysłaniem zapytania
            setLoading(true); // Ustawiamy stan ładowania, zanim fetch zostanie wykonane
            fetchFlightById(parsedId);  // Fetch flight by ID
        } else {
            alert('Podaj poprawne ID lotu');
        }
    };

    // Jeśli lot nie został znaleziony, ustawiamy notFound na true
    useEffect(() => {
        if (flight === null && id && loading) {
            setNotFound(true); // Ustawiamy błąd, gdy lot nie zostanie znaleziony
        }
    }, [flight, id, loading]);

    return (
        <div>
            <h2>Szukaj lotu po ID</h2>
            <input
                type="text"
                value={id}
                onChange={handleChange}
                placeholder="Wprowadź ID lotu"
            />
            <button onClick={handleFetchFlight}>Pobierz lot</button>

            {/* Komunikat o błędzie tylko po kliknięciu przycisku i braku lotu */}
            {notFound && !loading && <p>Nie znaleziono lotu o tym ID</p>} {/* Wyświetlamy tylko po kliknięciu przycisku */}

            {flight ? (
                <div>
                    <h3>Lot: {flight.flightNumber}</h3>
                    <p>Wylot: {flight.departureTime}</p>
                    <p>Status: {flight.status}</p>

                    {/* Display departure airport info if available */}
                    <p>Lotnisko
                        odlotu: {departureAirport ? `${departureAirport.name}, ${departureAirport.city.name}, ${departureAirport.city.country}` : 'Ładowanie...'}</p>

                    <p>Lotnisko
                        przylotu: {arrivalAirport ? `${arrivalAirport.name}, ${arrivalAirport.city.name}, ${arrivalAirport.city.country}` : 'Ładowanie...'}</p>

                </div>
            ) : (
                // Jeśli lotu nie ma i użytkownik nie wyszukał jeszcze lotu
                <div></div>
            )}
        </div>
    );
}

export default FlightDetails;
