import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FlightDetails({ flight, fetchFlightById, updateFlight, deleteFlight, fetchFlightsByStatus }) {
    const [id, setId] = useState('');
    const [status, setStatus] = useState('');
    const [departureAirport, setDepartureAirport] = useState(null);
    const [arrivalAirport, setArrivalAirport] = useState(null);
    const [notFound, setNotFound] = useState(false);
    const [loading, setLoading] = useState(false);

    const [updatedFlight, setUpdatedFlight] = useState({
        flightNumber: '',
        departureTime: '',
        arrivalTime: '',
        departureAirportId: '',
        arrivalAirportId: ''
    });

    // Stan dla ID lotu do usunięcia
    const [deleteId, setDeleteId] = useState('');

    const handleChange = (e) => {
        setId(e.target.value);
        setNotFound(false);
    };

    const handleDeleteChange = (e) => {
        setDeleteId(e.target.value);
    };

    const handleStatusChange = (e) => {
        setStatus(e.target.value);
    };

    const handleUpdateChange = (e) => {
        const { name, value } = e.target;
        setUpdatedFlight({
            ...updatedFlight,
            [name]: value
        });
    };

    const handleFetchFlight = async () => {
        const parsedId = parseInt(id, 10);
        if (!isNaN(parsedId)) {
            setNotFound(false);
            setLoading(true);
            try {
                const response = await fetchFlightById(parsedId);
                if (!response) {
                    setNotFound(true);
                }
            } catch (error) {
                setNotFound(true);
            }
            setLoading(false);
        } else {
            alert('Podaj poprawne ID lotu');
        }
    };

    const handleUpdateFlight = async () => {
        try {
            await updateFlight(id, updatedFlight);
        } catch (error) {
            alert('Wystąpił błąd podczas aktualizacji lotu');
        }
    };

    const handleDeleteFlight = async () => {
        const parsedId = parseInt(deleteId, 10);
        if (isNaN(parsedId)) {
            alert('Podaj poprawne ID lotu do usunięcia');
            return;
        }

        try {
            await deleteFlight(parsedId);
            alert('Lot został pomyślnie usunięty');
            setDeleteId(''); // Wyczyść pole ID po usunięciu
        } catch (error) {
            alert('Wystąpił błąd podczas usuwania lotu');
            console.error(error);
        }
    };

    const handleFetchFlightsByStatus = async () => {
        try {
            const response = await fetchFlightsByStatus(status);
            console.log('Loty o statusie:', response.data);
        } catch (error) {
            console.error('Błąd podczas pobierania lotów:', error);
        }
    };

    useEffect(() => {
        if (flight) {
            setUpdatedFlight({
                flightNumber: flight.flightNumber,
                departureTime: flight.departureTime,
                arrivalTime: flight.arrivalTime,
                departureAirportId: flight.departureAirport.id,
                arrivalAirportId: flight.arrivalAirport.id
            });

            setDepartureAirport(flight.departureAirport);
            setArrivalAirport(flight.arrivalAirport);
        }
    }, [flight]);

    return (
        <div>
            <h2>Szukaj lotu po ID</h2>
            <input
                type="text"
                value={id}
                onChange={handleChange}
                placeholder="Wprowadź ID lotu"
            />
            <button onClick={handleFetchFlight}>Pobierz lot</button>

            {notFound && !loading && <p>Nie znaleziono lotu o tym ID</p>}

            {/* Zawsze wyświetlamy przycisk i formularz do usuwania lotu */}
            <div>
                <h3>Usuń lot</h3>
                <input
                    type="text"
                    value={deleteId}
                    onChange={handleDeleteChange}
                    placeholder="Wprowadź ID lotu do usunięcia"
                />
                <button onClick={handleDeleteFlight} disabled={!deleteId || loading}>
                    Usuń lot
                </button>
            </div>

            {/* Jeżeli lot jest załadowany, wyświetl szczegóły */}
            {flight && (
                <div>
                    <h3>Lot: {flight.flightNumber}</h3>
                    <p>Wylot: {flight.departureTime}</p>
                    <p>Status: {flight.status}</p>
                    <p>Lotnisko odlotu: {departureAirport ? `${departureAirport.name}, ${departureAirport.city.name}, ${departureAirport.city.country}` : 'Ładowanie...'}</p>
                    <p>Lotnisko przylotu: {arrivalAirport ? `${arrivalAirport.name}, ${arrivalAirport.city.name}, ${arrivalAirport.city.country}` : 'Ładowanie...'}</p>
                </div>
            )}

            {/* Formularz aktualizacji */}
            <h3>Aktualizuj lot</h3>
            <input
                type="text"
                name="flightNumber"
                value={updatedFlight.flightNumber}
                onChange={handleUpdateChange}
                placeholder="Numer lotu"
            />
            <input
                type="text"
                name="departureTime"
                value={updatedFlight.departureTime}
                onChange={handleUpdateChange}
                placeholder="Czas wylotu"
            />
            <input
                type="text"
                name="arrivalTime"
                value={updatedFlight.arrivalTime}
                onChange={handleUpdateChange}
                placeholder="Czas przylotu"
            />
            <input
                type="number"
                name="departureAirportId"
                value={updatedFlight.departureAirportId}
                onChange={handleUpdateChange}
                placeholder="ID lotniska odlotu"
            />
            <input
                type="number"
                name="arrivalAirportId"
                value={updatedFlight.arrivalAirportId}
                onChange={handleUpdateChange}
                placeholder="ID lotniska przylotu"
            />
            <button onClick={handleUpdateFlight}>Zaktualizuj lot</button>

            {/* Wyszukaj loty po statusie */}
            <h3>Wyszukaj loty po statusie</h3>
            <select onChange={handleStatusChange} value={status}>
                <option value="">Wybierz status</option>
                <option value="ON_TIME">Na czas</option>
                <option value="DELAYED">Opóźniony</option>
                <option value="CANCELLED">Anulowany</option>
            </select>
            <button onClick={handleFetchFlightsByStatus}>Pobierz loty</button>
        </div>
    );
}

export default FlightDetails;
